import React, { useState } from 'react';  
import AppLayout from '../components/AppLayout';
import Head from 'next/head';
import{ Form, Input, Checkbox, Button } from 'antd';


const signup = () => { 
    const[id, setId] = useState('');
    const[nick, setNick] = useState('');
    const[pass, setPass] = useState('');
    const[passCheck , setPassCheck] = useState('');
    const[term, setTerm] = useState(false);

    const onSubmit = (e) => {
        console.log({id, nick, pass, passCheck,term });
        e.preventDefault();
        if(password!== passwordCheck){
            return setpasswordError(true);
        }
    };
    const onChangeId = (e) => {
        setId(e.target.value);
    };
    const onChangeNick = (e) => {
        setNick(e.target.value);
    };
    const onChangePass = (e) => {
        setPass(e.target.value);
    };
    const onChangePassCheck = (e) => {
        setPassCheck(e.target.value);
    };
    const onChagneTerm = (e) => {
        setTerm(e.target.checked);
    };

    return<>
        <Head>
            <title>head</title>
            <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/antd/3.16.2/antd.css"/>
        </Head>
        <AppLayout> <form>
            <ul>
                <li>
                    <label htmlFor="user-id">id</label>
                    <input name="user-id" value={id} required onChange={onChangeId} />
                </li>
                <li>
                    <label htmlFor="user-nick">nickname</label>
                    <input name="user-nick" value={nick} required onChange={onChangeNick} />
                </li>
                <li>
                    <label htmlFor="user-pass">password</label>
                    <input name="user-pass" value={pass} required onChange={onChangePass} />
                </li>
                <li>
                    <label htmlFor="user-pass-chk">confirm pass</label>
                    <input name="user-pass-chk" value={passCheck} required onChange={onChangePassCheck} />
                    {termError && <div>비밀번호가 일치하지 않습니다.</div>}
                </li>
                <li>
                    <Checkbox name="user-term" value={term} onChange={onChagneTerm}>
                        블라블라 내용에 동의합니다.
                    </Checkbox>
                    {termError && <div>약관에 동의하셔야 합니다.</div>}
                </li>
                <li>
                    <Button type="primary" htmlType="submit">가입하기</Button>
                </li>
            </ul>
        </form>
        </AppLayout> 
    </>

};

export default signup;
